package proj8;

public class StringHandling {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name = "Fassil";
		
		name.concat(" Alemayhu"); // Fassil Alemayhu
		
		System.out.println("My name is :" + name); //Fassil
		System.out.println("My name is :" + name.concat(" Alemayhu"));
		System.out.println("My name is :"+ name + " Alemayhu");
		

	}

}
